%spark 

final case class User(     
      id: String, 
      name: String, 
      screen_name: String, 
      location: String, 
      url: String, 
      description: String, 
      verified: Option[Boolean],
      followers_count: Option[Int],
      friends_count: Option[Int],
      listed_count: Option[Int], 
      favourites_count: Option[Int], 
      statuses_count: Option[Int], 
      created_at: String, 
      utc_offset: Option[Int],
      time_zone: String, 
      geo_enabled: Option[Boolean],
      lang: String, 
      profile_image_url_https: String
)

def convertToInt(str : String) : Option [Int] = {
    try {
        return Some(str.toInt)
    } catch {
        case e:Exception => return None
    }
}

def convertToBool(str : String) : Option [Boolean] = {
    try {
        return Some(str.toBoolean)
    } catch {
        case e:Exception => return None
    }
}

def mapper(line: String) : User = {
    val fields = line.split(",")
    User(
        fields(0),
        fields(1),
        fields(2),
        fields(3),
        fields(4),
        fields(5),
        convertToBool(fields(6)),
        convertToInt(fields(7)),
        convertToInt(fields(8)),
        convertToInt(fields(9)),
        convertToInt(fields(10)),        
        convertToInt(fields(11)),
        fields(12),
        convertToInt(fields(13)),
        fields(14),
        convertToBool(fields(15)),
        fields(16),
        fields(17)
    )
}

val users = sc.textFile("/data/users.csv").map(mapper)

%pig

Result = LIMIT Users 10;
Result = FOREACH Result GENERATE id, name, screen_name; 

DUMP Result;

%pig

Groups = GROUP Users ALL;
Result = FOREACH Groups GENERATE COUNT_STAR($1);

DUMP Result;

%pig.query

Hour = FOREACH Tweets GENERATE REGEX_EXTRACT(created_at,'(\\S{3}) (\\S{3}) (\\S{2}) (\\S{2}):(\\S{2}):(\\S{2}) (\\S{5})',4) as (hour:int), '1' as (count:int);
Grouped = GROUP Hour BY hour;
FOREACH Grouped GENERATE group as hour, COUNT(Hour) as cnt;

%pig

UserIds = FOREACH Tweets GENERATE user_id;
UserIds = DISTINCT UserIds;
Result = JOIN Users BY id, UserIds BY user_id;
Result = FOREACH Result GENERATE id, name, screen_name;
Result = DISTINCT Result;

DUMP Result;

%pig.query

UserInfo = FOREACH Users GENERATE id, screen_name; 
UniqUsers = DISTINCT UserInfo;
UserIds = FOREACH Tweets GENERATE user_id;
Joined = JOIN UniqUsers BY id, UserIds BY user_id;
Grouped = GROUP Joined By screen_name;
Result = FOREACH Grouped GENERATE group as screen_name, COUNT(Joined) as qty;
ResultOrdered = ORDER Result BY qty DESC;

LIMIT ResultOrdered 10;

%pig

REGISTER /data/tutorial.jar
Lowered = FOREACH Tweets GENERATE id, org.apache.pig.tutorial.ToLower(text) as tweet;
Ngram = FOREACH Lowered GENERATE id, flatten(org.apache.pig.tutorial.NGramGenerator(tweet)) as ngram;
Ngram = FILTER Ngram BY SIZE(ngram) > 6;
Ngram = DISTINCT Ngram;
Grouped = GROUP Ngram BY (ngram);
Frequency = FOREACH Grouped GENERATE group, COUNT(Ngram) as count;
MostUsed = FILTER Frequency BY count > 20;
Result = ORDER MostUsed BY count DESC;

DUMP Result;
